mapper.select:
select /*+INDEX(post PK_POST)*/ *
from post
where postNo = #{postNo}

select count (*)
from post
where boardNo = #{boardNo}

mapper.update:
update post
set postTitle = #{postTitle},
postContent = #{postContent}
where postNo = #{postNo}

mapper.delete:
update post
set postDelFlag = 'F'
where postNo = #{postNo}

mapper.select:
select count(*)
from comments
where postNo = #{postNo}

select *
from comment_like
where cmtNo = #{cmtNo} and memId = #{memId}

     select count(*)
	 from report 
	 where toMemId =#{toMemId} and fromMemId = #{fromMemId}
